import React from 'react'
import './style.css';

function Card(props) {
        return (

                <div className="col-3">
                    <div className="card ">
                        <img className="card-img hoverable" src={props.imgUrl} alt={props.name} onClick={() => props.shuffleArray(props.id)}/>
                    </div>
                </div>


        )

}

export default Card;
