import React from 'react'
import './style.css';

function Card(props) {

    handleIncrement = () => {
        this.setState({ count: this.state.count + 1 });
      };

        return (
                <div className="col-3">
                    <div className="card hoverable z-depth-1-half">
                        <img className="card-img" src={props.imgUrl} alt={props.name} onClick={this.handleIncrement.bind(this)}/>
                    </div>
                </div>


        )

}

export default Card;
