import React from 'react';
// components
import Jumbotron from './components/Jumbotron/Jumbotron';
import Card from './components/Card/Card';
import Counter from './components/Counter/Counter';
// img arrays
import ArrayOne from './JSON/arrayOne';
import ArrayTwo from './JSON/arrayTwo';
import ArrayThree from './JSON/arrayThree';

import Wrapper from './components/Wrapper/Wrapper';

class App extends React.Component {

  state = {
    firstArray : ArrayOne,
    secondArray: ArrayTwo,
    thirdArray: ArrayThree
  }

  shuffleArray = id => {
    let newShuffle = this.state.firstArray.filter(() => Math.random() - 0.5);
    console.log(newShuffle);
    // this.setState({firstArray: newShuffle});
  }


  render() {
    return (
      <div>
        <Counter /> 
        <Jumbotron onClick={() => this.shuffleArray()}/>

        <Wrapper>
        <div className="row">
          {this.state.firstArray.map(rowOne => (
            <Card 
              key={rowOne.id}
              id={rowOne.id}
              name={rowOne.name}
              imgUrl={rowOne.image}
            />
          ))}
        </div>
        <div className="row">
          {this.state.secondArray.map(rowTwo => (
            <Card 
              key={rowTwo.id}
              id={rowTwo.id}
              name={rowTwo.name}
              imgUrl={rowTwo.image}
            />
          ))}
        </div>
        <div className="row">
          {this.state.thirdArray.map(rowThree => (
            <Card 
              key={rowThree.id}
              id={rowThree.id}
              name={rowThree.name}
              imgUrl={rowThree.image}
            />
          ))}
        </div>
        </Wrapper>
      </div>
      );
  }

}

export default App;
