import React from "react";
import '../Header/style.css';

class Header extends React.Component {
  state = {
    count: 0
  };

  handleIncrement = () => {
    this.setState({ count: this.state.count + 1 });
  };

  render() {
    return (
      <nav className="navbar navbar-dark indigo">
        {/* <span className="navbar-text white-text">The Clicky Game</span> */}
          <a className="navbar-text" href="https://fontmeme.com/the-lord-of-the-rings-font/">
            <img className="clicky-title" src="https://fontmeme.com/permalink/190622/9abb645554248ae6a5b12de539fa6da3.png" alt="the-lord-of-the-rings-font" border="0" /> 
          </a>
          <button className="btn btn-primary" onClick={this.handleIncrement}>
            Increment
          </button>
        <span className="navbar-text white-text">Wins: 0 | Score: {this.state.count}</span>
      </nav>
    );
  }
}

export default Header;
