    //this.setState = {
    //   characters,
    //   Score: 0,
    //   Wins: 0,
    //   clicked: []
    // }


  render() {
    return (
      <div>
        <Header 
          score={this.state.score}
          wins={this.state.wins}
        /> 
        {/* <Jumbotron /> */}

        <Wrapper>
        <div className="row">
          {this.state.characters.map(image => (
            <Card 
              key={image.id}
              id={image.id}
              name={image.name}
              imgUrl={image.image}
            />
          ))}
        </div>

        </Wrapper>
      </div>
      );
  }

}

export default App;
